import Logic from "./Logic";
import "./index.css";
import { motion, AnimateSharedLayout, AnimatePresence } from "framer-motion";
import { Icon } from "semantic-ui-react";

function Technologies({ content }) {
  const { setUnfold, unfold, unfoldAnimation, rotationAnimation } = Logic();

  return (
    <motion.div animate={unfoldAnimation} className="technologies-panel">
      <AnimateSharedLayout>
        <AnimatePresence>{unfold ? content : ""}</AnimatePresence>
        <div className="dropdown-case">
          <motion.div animate={rotationAnimation}>
            <Icon
              onClick={() => setUnfold(!unfold)}
              name="chevron down"
              color="grey"
              size="big"
              style={{ cursor: "pointer" }}
            />
          </motion.div>
        </div>
      </AnimateSharedLayout>
    </motion.div>
  );
}

export default Technologies;
