function FilterLogic() {
  const cleanData = (data) => {
    const cleanedData = [
      "All",
      ...new Set(
        data.map((item) => {
          const list = [];
          item.technologies.map((type) => {
            console.log(type);
            list.push(type);
          });
          console.log(list);
          return list;
        })
      ),
    ];

    return cleanedData;
  };

  const filterCategories = (data, category) => {
    const newItems = data.filter((item) => item[data] === category);
    return newItems;
  };

  return {
    cleanData,
    filterCategories,
  };
}

export default FilterLogic;
