function FilterLogic() {
  const cleanData = (data) => {
    const list = [];
    const filteredData = data.map((item) => {
      item.technologies.map((type) => {
        console.log(type);
        list.push(type);
      });
      console.log(list);
      return list;
    });

    const cleanedData = ["All", ...new Set(filteredData)];

    return cleanedData;
  };

  const filterCategories = (data, category) => {
    const newItems = data.filter((item) => item[data] === category);
    return newItems;
  };

  return {
    cleanData,
    filterCategories,
  };
}

export default FilterLogic;
