import { useState } from "react";

function FilterLogic() {
  const cleanData = (data) => {
    const cleanedData = [
      "All",
      ...new Set(
        data.map((item) => item.technologies.map((itemInside) => itemInside))
      ),
    ];

    return cleanedData;
  };

  const filterCategories = (data, category) => {
    const newItems = data.filter((item) => item[data] === category);
    return newItems;
  };

  return {
    cleanData,
    filterCategories,
  };
}

export default FilterLogic;
