import ContinerCard from "../../components/CardComponent/ContinerCard";
import Dropdown from "../../components/Dropdown/Dropdown";
import Filter from "../../helpers/Filter/Filter";
import "./index.css";

import { Segment, Header, Image, Divider, Icon } from "semantic-ui-react";
import { useHistory } from "react-router-dom";
import { motion } from "framer-motion";

function Home({ data }) {
  const history = useHistory();

  const src =
    "https://res.cloudinary.com/smartrestaurantapi/image/upload/v1629384728/162075162_965618190870570_2873432039017907345_n_hkg18a.jpg";

  return (
    <div>
      <motion.header className="header-main-content">
        <Segment.Group raised>
          <Segment inverted>
            <Image src={src} circular size="medium" centered />
            <Header size="medium" textAlign="center">
              I am Juan, a passionate developer who aims to build great
              applications using multiple technologies.
            </Header>
          </Segment>
          <Divider />
          <Segment inverted>
            <p className="para">
              I started programming around my 15/16 years, using Java, this did
              not last too long, since I did not see progress with that
              language, so I abandoned it. After a few years I got to know CSS
              and web development, from there I started to experiment a little
              in this and to propagate JS, later I focused only on python and
              once I mastered I returned to JS to master React.
            </p>
            <motion.div
              style={{
                display: "flex",
                justifyContent: "center",
                width: "100%",
              }}
              animate={{ height: "auto" }}
              exit={{ height: "100vh" }}
              transition={{ duration: 2 }}
            >
              <Icon
                name="chevron down"
                size="large"
                link={true}
                onClick={() => history.push("/know-more")}
              />
            </motion.div>
          </Segment>
        </Segment.Group>
      </motion.header>
      <section>
        <Header size="huge" textAlign="center" style={{ padding: 20 }}>
          Proyectos
        </Header>
        <div className="grid-cards">
          <ContinerCard data={data}></ContinerCard>
        </div>
      </section>
      <footer className="footer">
        <Dropdown content={<Filter data={data} />} />
      </footer>
    </div>
  );
}

export default Home;
