import Data from "../../Data";
import CustomCard from "./CustomCard";
import DisplayedCard from "./DisplayedCard";

import { useState } from "react";
import { AnimateSharedLayout, AnimatePresence } from "framer-motion";

function ContinerCard() {
  const [contentId, setContentId] = useState(false);

  const handleClick = (value, setter, content) => {
    if (content) {
      setter(false);
      return;
    }
    setter(value);
  };

  return (
    <>
      <AnimateSharedLayout>
        <AnimatePresence>
          {contentId && <DisplayedCard />}
          {Data.map((card, index) => {
            const { img, title, date, description, time } = card;
            let id = title + index;
            return (
              <CustomCard
                img={img}
                title={title}
                date={date}
                description={description}
                time={time}
                cb={(value) => handleClick(value, setContentId, contentId)}
                id={id}
              />
            );
          })}
        </AnimatePresence>
      </AnimateSharedLayout>
    </>
  );
}

export default ContinerCard;
