import FilterLogic from "./FilterLogic";
import SpecialButton from "../../components/SpecialButton/SpecialButton";
import "./index.css";

import { Button, Icon, Label } from "semantic-ui-react";
import { useState } from "react";

function Filter({ data }) {
  const [filterData, setFilterData] = useState(data);
  const { cleanData, filterCategories } = FilterLogic();
  const newItems = (data, filter, setter, type) => {
    const newData = filter(data, type);
    setter(newData);
  };

  const test = cleanData(data);

  return (
    <div>
      <div className="buttons-filter-container">
        <Button
          basic={true}
          color="blue"
          inverted={true}
          onClick={() => setFilterData(data)}
        >
          All
        </Button>
        {test.map((category, index) => {
          return (
            <Button
              onClick={() =>
                newItems(filterData, filterCategories, setFilterData, category)
              }
              basic={true}
              color="blue"
              inverted={true}
              key={index}
            >
              {category}
            </Button>
          );
        })}
      </div>
      <div className="content-filter-container">
        {filterData.map((item) => {
          const { title, link } = item;
          return (
            <Button as="div" labelPosition="right">
              <Button basic color="blue">
                <Icon name="fork" />
                {title}
              </Button>
              <Label as="a" href={link} basic color="blue" pointing="left">
                GO
              </Label>
            </Button>
          );
        })}
      </div>
    </div>
  );
}

export default Filter;
