import FilterLogic from "./FilterLogic";
import "./index.css";

import { Button } from "semantic-ui-react";
import { useState } from "react";

function Filter({ data }) {
  const [filterData, setFilterData] = useState(data);
  const { cleanData, filterCategories } = FilterLogic();
  const newItems = (data, filter, setter, type) => {
    const newData = filter(data, type);
    setter(newData);
  };

  const test = cleanData(data);

  return (
    <div>
      <div>
        <Button
          basic={true}
          color="blue"
          inverted={true}
          onClick={() => setFilterData(data)}
        >
          All
        </Button>
        {test.map((category, index) => {
          return (
            <Button
              onClick={() =>
                newItems(filterData, filterCategories, setFilterData, category)
              }
              basic={true}
              color="blue"
              inverted={true}
              key={index}
            >
              {category}
            </Button>
          );
        })}
      </div>
      {filterData.map((item) => {
        const { title } = item;
        return <h4 style={{ color: "white" }}>{title}</h4>;
      })}
    </div>
  );
}

export default Filter;
