import { useState } from "react";

function FilterLogic({ data, type }) {
  const [filters, setFilters] = useState();

  const cleanData = (data) => {
    const cleanedData = [
      "All",
      ...new Set(data.map((item) => item.technologies)),
    ];

    setFilters(cleanedData);

    return cleanedData;
  };

  const filterCategories = (data, category) => {
    const newItems = data.filter((item) => item[data] === category);
    return newItems;
  };

  return {
    cleanData,
    filters,
    setFilters,
    filterCategories,
  };
}

export default FilterLogic;
