import Data from "../../Data";
import CustomCard from "./CustomCard";
import DisplayedCard from "./DisplayedCard";

import { useState } from "react";
import { AnimateSharedLayout, AnimatePresence } from "framer-motion";

function ContinerCard() {
  const [contentId, setContentId] = useState(false);

  const handleClick = (value, setter, content) => {
    if (content) {
      setter(false);
      return;
    }
    setter(value);
  };

  return (
    <>
      <AnimateSharedLayout type={"crossfade"}>
        <AnimatePresence>
          {contentId &&
            Data.filter((card) => card.title === contentId).map(
              (card, index) => {
                const { title, date, description, time, history } = card;
                let id = title + index;
                return (
                  <DisplayedCard
                    key={id}
                    title={title}
                    date={date}
                    description={description}
                    history={history}
                    time={time}
                    cb={(value) => {
                      handleClick(value, setContentId, contentId);
                    }}
                    id={title}
                    imgs={[
                      "https://res.cloudinary.com/smartrestaurantapi/image/upload/v1629389283/WhatsApp_Image_2021-08-19_at_12.52.52_zozhjm.jpg",
                      "https://res.cloudinary.com/smartrestaurantapi/image/upload/v1629389283/WhatsApp_Image_2021-08-19_at_12.52.52_zozhjm.jpg",
                    ]}
                  />
                );
              }
            )}
          {Data.map((card) => {
            const { mainImg, title, date, description, time } = card;
            return (
              <CustomCard
                key={title}
                img={mainImg}
                title={title}
                date={date}
                description={description}
                time={time}
                cb={(value) => {
                  handleClick(value, setContentId, contentId);
                }}
                id={title}
              />
            );
          })}
        </AnimatePresence>
      </AnimateSharedLayout>
    </>
  );
}

export default ContinerCard;
