import { useState } from "react";

function Filter({ data, type }) {
  const cleanedData = ["All", ...new Set(data.map((item) => item[type]))];

  const [filters, setFilters] = useState(cleanedData);

  return {
    cleanedData,
    filters,
    setFilters,
  };
}

export default Filter;
