function FilterLogic() {
  const cleanData = (data) => {
    const cleanedData = [
      "All",
      ...new Set(
        data.map((item) => {
          return item.technologies.map((type) => {
            let types = [];
            types.push(type);
            console.log(types);
            return types;
          });
        })
      ),
    ];

    return cleanedData;
  };

  const filterCategories = (data, category) => {
    const newItems = data.filter((item) => item[data] === category);
    return newItems;
  };

  return {
    cleanData,
    filterCategories,
  };
}

export default FilterLogic;
