import Data from "../../Data";
import CustomCard from "./CustomCard";
import DisplayedCard from "./DisplayedCard";

import { useState } from "react";
import { AnimateSharedLayout, AnimatePresence } from "framer-motion";

function ContinerCard() {
  const [contentId, setContentId] = useState(false);

  const handleClick = (value, setter, content) => {
    if (content) {
      setter(false);
      return;
    }
    setter(value);
  };

  return (
    <>
      <AnimateSharedLayout>
        <AnimatePresence>
          {contentId &&
            Data.filter((card) => card.title === contentId).map((card) => {
              const { mainImg, title, date, description, time, history } = card;
              return (
                <DisplayedCard
                  img={mainImg}
                  title={title}
                  date={date}
                  description={description}
                  history={history}
                  time={time}
                  cb={(value) => handleClick(value, setContentId, contentId)}
                  id={title}
                />
              );
            })}
          {Data.map((card, index) => {
            const { mainImg, title, date, description, time } = card;
            return (
              <CustomCard
                img={mainImg}
                title={title}
                date={date}
                description={description}
                time={time}
                cb={(value) => handleClick(value, setContentId, contentId)}
                id={title}
              />
            );
          })}
        </AnimatePresence>
      </AnimateSharedLayout>
    </>
  );
}

export default ContinerCard;
